# /base_app/core/email_tokens.py
from __future__ import annotations

from itsdangerous import URLSafeTimedSerializer, BadSignature, SignatureExpired

from .config import settings


def _serializer() -> URLSafeTimedSerializer:
    return URLSafeTimedSerializer(
        secret_key=settings.auth.email_verify_secret,
        salt="email-verify",
    )


def make_email_token(user_id: int) -> str:
    return _serializer().dumps({"uid": user_id})


def read_email_token(token: str) -> dict:
    max_age = settings.auth.verify_token_ttl_hours * 3600
    data = _serializer().loads(token, max_age=max_age)
    return data

# /base_app/core/logging.py
from __future__ import annotations

import json
import logging
from logging import LoggerAdapter
from time import time


class JsonFormatter(logging.Formatter):
    """Простой JSON-форматтер: время, уровень, логгер, сообщение/данные."""

    def format(self, record: logging.LogRecord) -> str:  # noqa: D401
        payload = {
            "ts": round(time(), 3),
            "level": record.levelname,
            "logger": record.name,
        }
        # Если передали dict в msg — включаем "как есть"
        if isinstance(record.msg, dict):
            payload.update(record.msg)
        else:
            payload["message"] = record.getMessage()
        return json.dumps(payload, ensure_ascii=False)


def get_logger(name: str, base_level: int = logging.INFO) -> LoggerAdapter:
    """Возвращает LoggerAdapter с JSON-форматированием."""
    logger = logging.getLogger(name)
    if not logger.handlers:
        handler = logging.StreamHandler()
        handler.setFormatter(JsonFormatter())
        logger.addHandler(handler)
    logger.setLevel(base_level)
    # LoggerAdapter позволяет передавать .info({...}) и добавлять extra при необходимости
    return LoggerAdapter(logger, extra={})

# /base_app/views/auth.py
from __future__ import annotations

import secrets
from pathlib import Path
from typing import Annotated, Optional

from fastapi import APIRouter, Request, Depends, Form, BackgroundTasks, status, UploadFile, File
from fastapi.responses import RedirectResponse
from fastapi.templating import Jinja2Templates
from sqlalchemy.ext.asyncio import AsyncSession
from sqlalchemy.exc import IntegrityError

from base_app.core.models import db_helper
from base_app.core.logging import get_logger
from base_app.mailing.email import send_verification_email_sync
from base_app.services.auth_service import AuthService

router = APIRouter()
log = get_logger("views.auth")

TEMPLATES_DIR = Path(__file__).resolve().parents[2] / "templates"
templates = Jinja2Templates(directory=str(TEMPLATES_DIR))


def _ensure_csrf(request: Request) -> str:
    token = request.session.get("csrf")
    if not token:
        token = secrets.token_urlsafe(16)
        request.session["csrf"] = token
    return token


def _new_captcha(request: Request) -> tuple[int, int, int]:
    a = secrets.randbelow(9) + 1
    b = secrets.randbelow(9) + 1
    s = a + b
    request.session["captcha_sum"] = s
    return a, b, s


@router.get("/auth/login", name="login_html")
async def login_html(request: Request):
    csrf = _ensure_csrf(request)
    a, b, _ = _new_captcha(request)
    return templates.TemplateResponse("core/login.html", {"request": request, "csrf": csrf, "a": a, "b": b})


@router.post("/auth/login", name="login_post_html")
async def login_post_html(
    request: Request,
    session: Annotated[AsyncSession, Depends(db_helper.session_getter)],
    email: Annotated[str, Form(...)],
    password: Annotated[str, Form(...)],
    csrf_token: Annotated[str, Form(...)],
    captcha: Annotated[int, Form(...)],
):
    if csrf_token != request.session.get("csrf"):
        return templates.TemplateResponse(
            "core/login.html",
            {"request": request, "csrf": _ensure_csrf(request),
             "alert": {"kind": "error", "text": "CSRF error"}},
            status_code=400,
        )

    try:
        if int(captcha) != int(request.session.get("captcha_sum", -1)):
            raise ValueError
    except Exception:
        return templates.TemplateResponse(
            "core/login.html",
            {"request": request, "csrf": _ensure_csrf(request),
             "alert": {"kind": "error", "text": "Капча неверна"}},
            status_code=400,
        )

    service = AuthService()
    try:
        token = await service.authenticate(session, email=email.strip().lower(), password=password)
    except ValueError:
        return templates.TemplateResponse(
            "core/login.html",
            {"request": request, "csrf": _ensure_csrf(request),
             "alert": {"kind": "error", "text": "Неверный e-mail или пароль"}},
            status_code=401,
        )
    except Exception as e:
        log.info({"event": "login_fail", "email": email, "error": str(e)})
        return templates.TemplateResponse(
            "core/login.html",
            {"request": request, "csrf": _ensure_csrf(request),
             "alert": {"kind": "error", "text": "Вход временно недоступен"}},
            status_code=500,
        )

    # Сохраняем токен в сессии — шапка покажет «Профиль/Выход»
    request.session["access_token"] = token
    request.session["user_email"] = email.strip().lower()

    log.info({"event": "login_ok", "email": email})
    return RedirectResponse(url="/", status_code=status.HTTP_303_SEE_OTHER)


@router.get("/auth/register", name="register_html")
async def register_html(request: Request):
    csrf = _ensure_csrf(request)
    a, b, _ = _new_captcha(request)
    return templates.TemplateResponse("core/register.html", {"request": request, "csrf": csrf, "a": a, "b": b})


@router.post("/auth/register", name="register_post_html")
async def register_post_html(
    request: Request,
    background: BackgroundTasks,
    session: Annotated[AsyncSession, Depends(db_helper.session_getter)],
    email: Annotated[str, Form(...)],
    password: Annotated[str, Form(...)],
    password2: Annotated[str, Form(...)],
    csrf_token: Annotated[str, Form(...)],
    captcha: Annotated[int, Form(...)],
):
    if csrf_token != request.session.get("csrf"):
        return templates.TemplateResponse(
            "core/register.html",
            {"request": request, "csrf": _ensure_csrf(request), "alert": {"kind": "error", "text": "CSRF error"}},
            status_code=400,
        )
    try:
        if int(captcha) != int(request.session.get("captcha_sum", -1)):
            raise ValueError
    except Exception:
        return templates.TemplateResponse(
            "core/register.html",
            {"request": request, "csrf": _ensure_csrf(request), "alert": {"kind": "error", "text": "Капча неверна"}},
            status_code=400,
        )

    email = email.strip().lower()
    if len(password) < 8 or len(password) > 256 or password != password2:
        msg = "Пароль должен быть 8..256 символов и совпадать."
        return templates.TemplateResponse(
            "core/register.html",
            {"request": request, "csrf": _ensure_csrf(request), "alert": {"kind": "error", "text": msg}},
            status_code=400,
        )

    service = AuthService()
    try:
        user_id, verify_token = await service.register_user(session, email=email, password=password)
        await session.commit()
    except ValueError as e:
        if str(e) == "email_already_exists":
            log.info({"event": "register_fail", "email": email, "reason": "precheck_exists"})
            return templates.TemplateResponse(
                "core/register.html",
                {"request": request, "csrf": _ensure_csrf(request),
                 "alert": {"kind": "error", "text": "Пользователь с таким e-mail уже существует"}},
                status_code=400,
            )
        await session.rollback()
        log.info({"event": "register_fail", "email": email, "error": str(e)})
        return templates.TemplateResponse(
            "core/register.html",
            {"request": request, "csrf": _ensure_csrf(request),
             "alert": {"kind": "error", "text": f"Ошибка регистрации: {e}"}},
            status_code=400,
        )
    except IntegrityError as e:
        await session.rollback()
        txt = str(getattr(e, "orig", e))
        log.info({"event": "register_fail", "email": email, "sql_error": txt})
        return templates.TemplateResponse(
            "core/register.html",
            {"request": request, "csrf": _ensure_csrf(request),
             "alert": {"kind": "error", "text": "Нарушение целостности данных (уникальность/NOT NULL)."}},
            status_code=400,
        )
    except Exception as e:
        await session.rollback()
        log.info({"event": "register_fail", "email": email, "error": str(e)})
        return templates.TemplateResponse(
            "core/register.html",
            {"request": request, "csrf": _ensure_csrf(request),
             "alert": {"kind": "error", "text": "Не удалось зарегистрировать пользователя"}},
            status_code=400,
        )

    verify_link = str(request.url_for("verify_email", token=verify_token))
    log.info({"event": "verify_link", "email": email, "verify_link": verify_link})
    background.add_task(send_verification_email_sync, email, verify_link)

    success_alert = {
        "kind": "success",
        "text": "Мы отправили письмо с подтверждением. Перейдите по ссылке из письма, чтобы завершить регистрацию.",
    }
    return templates.TemplateResponse("core/index.html", {"request": request, "alert": success_alert})


@router.post("/auth/logout", name="logout_html")
async def logout_html(request: Request):
    request.session.pop("access_token", None)
    request.session.pop("user_email", None)
    request.session.clear()
    log.info({"event": "logout"})
    return RedirectResponse(url="/", status_code=status.HTTP_303_SEE_OTHER)

/* /static/css/style.css */

/* 1) Variables */
:root {
  --gap: 16px;
  --radius: 10px;
  --ok: #19a974;
  --ok-bg: #e6fbf2;
  --err: #d33;
  --err-bg: #fdecec;
}

/* 2) Base */
*,
*::before,
*::after {
  box-sizing: border-box;
}
body {
  margin: 0;
  font-family: system-ui, -apple-system, "Segoe UI", Roboto, sans-serif;
  color: #111;
  background: #fafafa;
}
a {
  color: #0366d6;
  text-decoration: none;
}
a:hover {
  text-decoration: underline;
}

/* 3) Layout */
.container {
  max-width: 1000px;
  margin: 0 auto;
  padding: var(--gap);
}

/* Header / Navbar */
.header {
  background: #fff;
  border-bottom: 1px solid #eee;
}
.header-inner {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: var(--gap);
}
.brand {
  font-weight: 700;
}
.nav-list {
  display: flex;
  align-items: center;
  gap: 12px;
  list-style: none;
  margin: 0;
  padding: 0;
}
.nav-item {
  display: flex;
  align-items: center;
}
.nav-logout {
  background: none;
  border: 0;
  padding: 0;
  cursor: pointer;
  text-decoration: underline;
  font: inherit;
}

/* Back-compat для старой разметки шапки */
.navbar {
  display: flex;
  align-items: center;
  gap: var(--gap);
}
.navbar .brand {
  font-weight: 700;
}
.spacer {
  flex: 1;
}

/* 4) Tables */
.table {
  width: 100%;
  border-collapse: collapse;
}
.table th,
.table td {
  border: 1px solid #ddd;
  padding: 8px;
}
.table th {
  background: #f3f3f3;
  text-align: left;
}

/* 5) Forms */
.form-row {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 12px;
}
.form-row > label {
  flex: 0 0 180px; /* фиксированная ширина подписи */
}
.form-row > input,
.form-row > select,
.form-row > textarea {
  flex: 1 1 auto;
  min-width: 0;
}
input[type="text"],
input[type="email"],
input[type="password"],
input[type="number"],
select,
textarea {
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: var(--radius);
  background: #fff;
}

/* 6) Buttons */
.btn {
  padding: 8px 14px;
  border-radius: var(--radius);
  border: 1px solid #ccc;
  background: #fff;
  cursor: pointer;
}
.btn:hover {
  filter: brightness(0.98);
}
.btn-primary {
  border-color: #0366d6;
  background: #0366d6;
  color: #fff;
}

/* 7) Alerts */
.alert {
  margin-bottom: var(--gap);
  padding: 12px 14px;
  border-radius: var(--radius);
  border: 2px solid transparent;
}
.alert.success {
  border-color: var(--ok);
  background: var(--ok-bg);
}
.alert.error {
  border-color: var(--err);
  background: var(--err-bg);
}

